<?php/* *THIS CLASS IS USED TO PERFORM THE FUNCTIONS OF RETRIEVING USER DETAILS * FROM THE USERBASE. * */class User {	function get_finder_details($user) {		include 'dbconn.inc.php';		$query = "SELECT login.username,user_phoneno.phone_number FROM login INNER JOIN user_phoneno ON login.user_id=user_phoneno.user_id WHERE login.username='" . $user . "' LIMIT 1";		$result = mysql_query($query);		echo "you are the finder of the item the following details about you as the finder will be provided:<p/>";		echo "<table  align='center' width='300'>";		$content = '';		while ($row = mysql_fetch_array($result)) {			$username = $row['username'];			$phonenumber = $row['phone_number'];			$content .= <<<EOD		 <tr>		 <td>Found by:</td>		 <td><b>$username<b/></td>		 </tr>		 		 <tr>		 <td>Phone number</td>		 <td><b>$phonenumber<b/></td>		 </tr>EOD;		}		echo $content;		echo "</table>";	}	function get_user_id($user) {		include "dbconn.inc.php";		$query = "SELECT user_id FROM login WHERE username='" . $user . "'";		$result = mysql_query($query);		$uid = '';		while ($row = mysql_fetch_array($result)) {			$uid = $row['user_id'];		}		return $uid;	}	function get_user_names($user) {		include "dbconn.inc.php";		$query = "SELECT names FROM users WHERE user_id='" . $user . "'";		$result = mysql_query($query);		$usernames = '';		while ($row = mysql_fetch_array($result)) {			$usernames = $row['names'];		}		return $usernames;	}	function get_user_email($user) {		include "dbconn.inc.php";		$query = "SELECT emailadress FROM user_email WHERE user_id='" . $user . "'";		$result = mysql_query($query);		$useremail = '';		while ($row = mysql_fetch_array($result)) {			$useremail = $row['emailadress'];		}		return $useremail;	}	function get_user_number($user) {		include "dbconn.inc.php";		$query = "SELECT phone_number FROM user_phoneno WHERE user_id='" . $user . "'";		$result = mysql_query($query);		$number = '';		while ($row = mysql_fetch_array($result)) {			$number = $row['phone_number'];		}		return $number;	}	function get_user_location($user) {		include "dbconn.inc.php";		$query = "SELECT location FROM users WHERE user_id='" . $user . "'";		$result = mysql_query($query);		$location = '';		while ($row = mysql_fetch_array($result)) {			$location = $row['location'];		}		return $location;	}	function get_user_age($user) {		include "dbconn.inc.php";		$query = "SELECT yob FROM users WHERE user_id='" . $user . "'";		$result = mysql_query($query) or die(mysql_error());		$yob = '';		while ($row = mysql_fetch_array($result)) {			$yob = $row['yob'];		}		$currentyear = date("Y");		$age = $currentyear - $yob;		return $age;	}	function get_yob($user) {		include "dbconn.inc.php";		$query = "SELECT yob FROM users WHERE user_id='" . $user . "'";		$result = mysql_query($query);		$yob = '';		while ($row = mysql_fetch_array($result)) {			$yob = $row['yob'];		}		return $yob;	}	function get_user_gender($user) {		include "dbconn.inc.php";		$query = "SELECT gender FROM users WHERE user_id='" . $user . "'";		$result = mysql_query($query);		$gender = '';		while ($row = mysql_fetch_array($result)) {			$gender = $row['gender'];		}		return $gender;	}	function total_users() {		$queryallusers = "SELECT * FROM users";		$result = mysql_query($queryallusers);		$totalusers = mysql_num_rows($result);		return $totalusers;	}	function ptk_uniq_id() {		$random = rand(100, 1000000);		return uniqid($random);	}	//get the users current information	function returnAccountInfo(){		require_once("dbconn.inc.php");		$user = $_SESSION['user_allow'];		$uid = $this->get_user_id($user);						$query = "SELECT username FROM login WHERE user_id='" . $uid . "'";		$result = mysql_query($query);		$content = '';		while ($row = mysql_fetch_array($result)) {			$username = $row['username'];			$content .= <<<EOD<table align="left" width=100%><tr><td><b>Username:</b></td><td>$username</td></tr></table></div>EOD;	}		echo $content;	}			//get the users basic personal information	function returnBasicInfo(){				require_once("dbconn.inc.php");		$user = $_SESSION['user_allow'];		$uid = $this->get_user_id($user);				$query = "SELECT * FROM users where user_id='" . $uid . "'";		$result = mysql_query($query);		$content = '';		echo "<table  style='margin-top:10%;'>"; 		while ($row = mysql_fetch_array($result)) {			$names = $row['names'];			$location = $row['location'];			$age = $row['yob'];			$gender = $row['gender'];			$content .= <<<EOD	<tr height="50">	<td><b>Names<b/></td>	<td>$names</td>	</tr>	<tr height="50">	<td><b>Location:</b></td>	<td>$location</td>	</tr>	<tr height="50">	<td><b>Age</b></td>	<td>$age</td>	</tr>	<tr height='50'>	<td><b>Gender</b></td>	<td>$gender</td>	</tr>	EOD;		}		echo $content;		echo "<table/>";	}}?>